flow.chain:
THEN(
    // 1. 前置过滤器链
    ConcurrencySafetyLockFilter,
    DataPreparationFilter,
    IF(CheckProceed, BlacklistRaffleFilter),
    IF(CheckProceed, SpecialTimeMatchRafflePoolFilter),
    IF(CheckProceed, NormalTimeMatchRafflePoolFilter),
    THEN(
        // 2. 路由
        RaffleFilterRouter,
        // 3. 后置过滤器链
        UpdateExpireTimeFilter,
        AwardInventoryFilter,
        UserRaffleTimeFilter,
        UserRaffleFlowRecordFilter,
        ConcurrencySafetyUnLockFilter
    ).retry(3, "app.xlog.ggbond.raffle.model.vo.RetryRouterException")
);

THEN(
    SWITCH(ActivityOrderTypeRouter).TO(
        IF(SignInToClaimJudge, CreateActivityOrderSuccess, CreateActivityOrderFail),
        IF(FreeGiveawayJudge, CreateActivityOrderSuccess, CreateActivityOrderFail)
    )
);
